widgetTemplates.storefront_api_query:
query ProductSet($productIds: [Int!], $activeCurrencyCode: currencyCode!) {
    site {
        settings {
            storeName
            tax {
                plp
            }
            inventory {
                defaultOutOfStockMessage
            }
            storefront {
                catalog {
                    productComparisonsEnabled
                }
            }
        }
        currency(currencyCode: $activeCurrencyCode) {
            display {
                symbol
                symbolPlacement
                decimalToken
                decimalPlaces
                thousandsToken
            }
        }
        products(entityIds: $productIds, first: 50) {
            edges {
                node {
                    brand {
                        name
                    }
                    name
                    entityId
                    addToCartUrl
                    addToWishlistUrl
                    path
                    sku
                    showCartAction
                    minPurchaseQuantity
                    maxPurchaseQuantity
                    plainTextDescription(characterLimit: 1000)
                    inventory {
                        isInStock
                    }
                    availabilityV2 {
                        status
                    }
                    priceWithTax: prices(includeTax: true, currencyCode: $activeCurrencyCode)  {
                        price {
                            currencyCode
                            value
                            formatted
                        }
                        salePrice {
                            currencyCode
                            value
                            formatted
                        }
                        basePrice {
                            currencyCode
                            value
                            formatted
                        }
                    }
                    priceWithoutTax: prices(includeTax: false, currencyCode: $activeCurrencyCode)  {
                        price {
                            currencyCode
                            value
                            formatted
                        }
                        salePrice {
                            currencyCode
                            value
                            formatted
                        }
                        basePrice {
                            currencyCode
                            value
                            formatted
                        }
                    }
                    reviewSummary {
                        summationOfRatings
                        numberOfReviews
                    }
                    defaultImage {
                        url80wide: url(width: 80)
                        url160wide: url(width: 160)
                        url320wide: url(width: 320)
                        url640wide: url(width: 640)
                        url960wide: url(width: 960)
                        url1280wide: url(width: 1280)
                        url1920wide: url(width: 1920)
                    }
                    productOptions {
                        edges {
                            node {
                                entityId
                            }
                        }
                    }
                }
            }
        }
    }
}

