{{#each merchant_info as |merchant|}}
{{#if merchant.currency '===' ../_.context.activeCurrencyCode}}
<style media="screen">
  #banner-region-{{../_.id}} {
    display: block;
    width: 100%;
    max-width: 545px;
  }
</style>

<div id="banner-region-{{../_.id}}">
  <div
    data-pp-style-logo-position="{{#if ../style.bt-proddetailspage-quarterbanner.data-pp-style-logo-position}}{{../style.bt-proddetailspage-quarterbanner.data-pp-style-logo-position}}{{else}}left{{/if}}"
    data-pp-style-logo-type="{{#if ../style.bt-proddetailspage-quarterbanner.data-pp-style-logo-type}}{{../style.bt-proddetailspage-quarterbanner.data-pp-style-logo-type}}{{else}}inline{{/if}}"
    data-pp-style-text-color="{{#if ../style.bt-proddetailspage-quarterbanner.data-pp-style-text-color}}{{../style.bt-proddetailspage-quarterbanner.data-pp-style-text-color}}{{else}}black{{/if}}"
    data-pp-style-text-size="{{#if ../style.bt-proddetailspage-quarterbanner.data-pp-style-text-size}}{{../style.bt-proddetailspage-quarterbanner.data-pp-style-text-size}}{{else}}12{{/if}}"
    id="paypal-bt-proddetails-messaging-banner">
  </div>
  <script defer nonce="{{nonce}}">
    (function () {
      // Price container with data-product-price-with-tax has bigger priority for product amount calculations
      const priceContainer = document.querySelector('[data-product-price-with-tax]')?.innerText
              ? document.querySelector('[data-product-price-with-tax]')
              : document.querySelector('[data-product-price-without-tax]');

      if (priceContainer) {
        renderMessageIfPossible();

        const config = {
          childList: true,    // Observe direct child elements
          subtree: true,      // Observe all descendants
          attributes: false,  // Do not observe attribute changes
        };

        const observer = new MutationObserver((mutationsList) => {
          if (mutationsList.length > 0) {
            renderMessageIfPossible();
          }
        });

        observer.observe(priceContainer, config);
      }

      function renderMessageIfPossible() {
        const amount = getPriceAmount(priceContainer.innerText)[0];

        if (amount) {
          renderMessage(amount);
        }
      }

      function getPriceAmount(text) {
        /*
         INFO:
         - extracts a string from a pattern: [number][separator][number][separator]...
           [separator] - any symbol but a digit, including whitespaces
         */
        const pattern = /\d+(?:[^\d]\d+)+(?!\d)/g;
        const matches = text.match(pattern);
        const results = [];

        // if there are no matches
        if (!matches?.length) {
          const amount = text.replace(/[^\d]/g, '');

          if (!amount) return results;

          results.push(amount);

          return results;
        }

        matches.forEach(str => {
          const sepIndexes = [];

          for (let i = 0; i < str.length; i++) {
            // find separator index and push to array
            if (!/\d/.test(str[i])) {
              sepIndexes.push(i);
            }
          }
          if (sepIndexes.length === 0) return;
          // assume the last separator is the decimal
          const decIndex = sepIndexes[sepIndexes.length - 1];

          // cut the substr to the separator with replacing non-digit chars
          const intSubstr = str.slice(0, decIndex).replace(/[^\d]/g, '');
          // cut the substr after the separator with replacing non-digit chars
          const decSubstr = str.slice(decIndex + 1).replace(/[^\d]/g, '');

          if (!intSubstr) return;

          const amount = decSubstr ? `${intSubstr}.${decSubstr}` : intSubstr;
          // to get floating point number
          const number = parseFloat(amount);

          if (!isNaN(number)) {
            results.push(String(number));
          }
        });

        return results;
      }

      function renderMessage(amount) {
        const messagesOptions = {
          amount,
          placement: 'product',
        };

        const paypalScriptParams = {
          'client-id': '{{merchant.clientId}}',
          components: ['messages'],
          'data-partner-attribution-id': '{{../attribution_id}}',
          'merchant-id': '{{merchant.merchantId}}',
          'data-namespace': 'paypalMessages'
        };

        if (!window.paypalMessages) {
          loadScript(paypalScriptParams).then(() => {
            window.paypalMessages.Messages(messagesOptions).render('#paypal-bt-proddetails-messaging-banner');
          });
        } else {
          window.paypalMessages.Messages(messagesOptions).render('#paypal-bt-proddetails-messaging-banner');
        }
      }

      function loadScript(paypalScriptParams) {
        return new Promise((resolve, reject) => {
          const script = document.createElement('script');
          script.type = 'text/javascript';
          script.src = 'https://unpkg.com/@paypal/paypal-js@3.1.6/dist/paypal.browser.min.js';
          script.integrity = 'sha384-ixafT4J9V4G5ltzIzhcmeyo1TvVbMf3eH73We7APYo338opA0FlgZ5VtBzMnMEEg';
          script.crossOrigin = 'anonymous';
          script.nonce = '{{nonce}}';

          script.onload = () =>
            window.paypalLoadScript(paypalScriptParams).then(() => resolve());

          script.onerror = event => {
            reject(event);
          };

          document.head.appendChild(script);
        });
      }
    }());
  </script>
</div>
{{/if}}
{{/each}}

