  <script>
    (function (window) {
      "use strict";

      var xhr = new XMLHttpRequest();
      xhr.addEventListener('readystatechange', function () {
        if (xhr.readyState === XMLHttpRequest.DONE) {
          window.zip_widget_merchantId = '{{ merchant_id }}';
          window.zip_widget_env = '{{ environment }}';
          window.zip_widget_region = '{{ country_code }}';
        {{#if minimum_price}}
          window.zip_widget_min_price = '{{ minimum_price }}';
        {{/if}}
        {{#if maximum_price}}
          window.zip_widget_max_price = '{{ maximum_price }}';
        {{/if}}
          window.zip_widget_product_widget ="#zip-nz-gb-widget";
          window.zip_widget_display_inline = true;
          try {
            window.zip_widget_product_price = JSON.parse(xhr.responseText)[0].cartAmount;
          } catch (err) {}
          window.Zip.Widget.render();
        }
      });
      xhr.open('GET', window.location.origin + '/api/storefront/carts');
      xhr.send();

      var dataCartTotals = document.querySelector('[data-cart-totals]');

      if (dataCartTotals) {
        var removeSymbols = function removeSymbols(price) {
          return ['NZ$', '$', '', ','].reduce(function (price, symbol) {
            return price.replaceAll(symbol, '');
          }, price);
        };
        var priceRegExp = /^\d+\.\d{2}/;

        var observer = new MutationObserver(function (mutationsList) {
          mutationsList.forEach(function (mutation) {
            var cartTotalGrandTotal = mutation.target.querySelector('.cart-total-grandTotal');

            window.zip_widget_product_price = cartTotalGrandTotal ? priceRegExp.exec(removeSymbols(cartTotalGrandTotal.innerText))[0] : '0';
            window.Zip.Widget.render();
          });
        });

        observer.observe(dataCartTotals, { childList: true });
      }
    })(window);
  </script>
