  <script>
    (function (window) {
      "use strict";

      window.zip_widget_merchantId = '{{ merchant_id }}';
      window.zip_widget_env = '{{ environment }}';
      window.zip_widget_region = '{{ country_code }}';
    {{#if minimum_price}}
      window.zip_widget_min_price = '{{ minimum_price }}';
    {{/if}}
    {{#if maximum_price}}
      window.zip_widget_max_price = '{{ maximum_price }}';
    {{/if}}
      window.zip_widget_product_widget ="#zip-nz-gb-widget";
      window.zip_widget_display_inline = true;
      var productPrice = document.querySelector('.productView .price.price--withoutTax') || document.querySelector('.productView .price.price--withTax');

      if (productPrice) {
        var removeSymbols = function removeSymbols(price) {
          return ['NZ$', '$', '', ','].reduce(function (price, symbol) {
            return price.replaceAll(symbol, '');
          }, price);
        };
        var numberRegExp = '\\d+\\.\\d{2}';
        var priceRangeRegExp = new RegExp('^' + numberRegExp + '\\s\\-\\s' + numberRegExp +'$');
        var priceRegExp = new RegExp('^' + numberRegExp +'$');
        var parsePrice = function parsePrice(price) {
          var priceNumber = removeSymbols(price);

          return priceRangeRegExp.test(priceNumber) ? '0' : priceRegExp.exec(priceNumber)[0];
        };

        var observer = new MutationObserver(function (mutationsList) {
          mutationsList.forEach(function (mutation) {
            window.zip_widget_product_price = parsePrice(mutation.target.innerText);
            window.Zip.Widget.render();
          });
        });

        observer.observe(productPrice, { childList: true });

        window.zip_widget_product_price = parsePrice(productPrice.innerText);
      }
    })(window);
  </script>
  <div id="zip-nz-gb-widget"></div>
