  #banner-region-{{ ../_.id }} {
    display: block;
    margin-right: auto;
    margin-left: auto;
    width: 100%;
    max-width: 728px;
  }
</style>

<div id="banner-region-{{ ../_.id }}">
  <div
    data-pp-message
    data-pp-style-layout="flex"
    data-pp-style-color="{{#if ../style.ppc-homepage-fullbanner.data-pp-style-color}}{{../style.ppc-homepage-fullbanner.data-pp-style-color}}{{else}}white{{/if}}"
    data-pp-style-ratio="{{#if ../style.ppc-homepage-fullbanner.data-pp-style-ratio}}{{../style.ppc-homepage-fullbanner.data-pp-style-ratio}}{{else}}8x1{{/if}}"
    id="paypal-commerce-homepage-messaging-banner">
  </div>
  <script defer nonce="{{nonce}}">
    function renderMessage() {
      const params = {
        'merchant-id': '{{merchant.merchantId}}',
        'client-id': '{{merchant.clientId}}',
        'data-partner-attribution-id': '{{../attribution_id}}',
        components: ['messages', 'buttons'],
      };

      if (params['client-id'] && params['merchant-id']) {
        window.paypalLoadScript(params)
          .then(paypal => {
            paypal.Messages({placement: 'home'}).render('#paypal-commerce-homepage-messaging-banner');
          });
      }
    }

    (function() {
      if (window.paypalLoadScript) {
        renderMessage();
        return;
      }

      const script = document.createElement('script');
      script.type = 'text/javascript';
      script.src = 'https://unpkg.com/@paypal/paypal-js@4.1.0/dist/iife/paypal-js.min.js';
      script.onload = function() {
        if (window.paypalLoadScript) {
          renderMessage();
        }
      };

      const container = document.getElementById(`banner-region-{{ ../_.id }}`);

      if (container) {
        container.appendChild(script);
      }
    })();
  </script>
</div>
{{/if}}
{{/each}}

{{#each merchant_info as |merchant|}}
{{#if merchant.currency '===' ../_.context.activeCurrencyCode}}
<style media="screen">
  #banner-region-{{ ../_.id }} {
    display: block;
    width: 100%;
    max-width: 545px;
  }
</style>

<div id="banner-region-{{ ../_.id }}">
  <div
    data-pp-message
    data-pp-style-logo-position="{{#if ../style.ppc-proddetailspage-quarterbanner.data-pp-style-logo-position}}{{../style.ppc-proddetailspage-quarterbanner.data-pp-style-logo-position}}{{else}}left{{/if}}"
    data-pp-style-logo-type="{{#if ../style.ppc-proddetailspage-quarterbanner.data-pp-style-logo-type}}{{../style.ppc-proddetailspage-quarterbanner.data-pp-style-logo-type}}{{else}}inline{{/if}}"
    data-pp-style-text-color="{{#if ../style.ppc-proddetailspage-quarterbanner.data-pp-style-text-color}}{{../style.ppc-proddetailspage-quarterbanner.data-pp-style-text-color}}{{else}}black{{/if}}"
    data-pp-style-text-size="{{#if ../style.ppc-proddetailspage-quarterbanner.data-pp-style-text-size}}{{../style.ppc-proddetailspage-quarterbanner.data-pp-style-text-size}}{{else}}12{{/if}}"
    id="paypal-commerce-proddetails-messaging-banner">
  </div>
  <script defer nonce="{{nonce}}">
    (function () {
      // Price container with data-product-price-with-tax has bigger priority for product amount calculations
      const priceContainer = document.querySelector('[data-product-price-with-tax]')?.innerText
        ? document.querySelector('[data-product-price-with-tax]')
        : document.querySelector('[data-product-price-without-tax]');

      if (priceContainer) {
        renderMessageIfPossible();

        const config = {
          childList: true,    // Observe direct child elements
          subtree: true,      // Observe all descendants
          attributes: false,  // Do not observe attribute changes
        };

        const observer = new MutationObserver((mutationsList) => {
          if (mutationsList.length > 0) {
            renderMessageIfPossible();
          }
        });

        observer.observe(priceContainer, config);
      }

      function renderMessageIfPossible() {
        const amount = getPriceAmount(priceContainer.innerText)[0];

        if (amount) {
          renderMessage({ amount });
        }
      }

      function getPriceAmount(text) {
        /*
         INFO:
         - extracts a string from a pattern: [number][separator][number][separator]...
           [separator] - any symbol but a digit, including whitespaces
         */
        const pattern = /\d+(?:[^\d]\d+)+(?!\d)/g;
        const matches = text.match(pattern);
        const results = [];

        // if there are no matches
        if (!matches?.length) {
          const amount = text.replace(/[^\d]/g, '');

          if (!amount) return results;

          results.push(amount);

          return results;
        }

        matches.forEach(str => {
          const sepIndexes = [];

          for (let i = 0; i < str.length; i++) {
            // find separator index and push to array
            if (!/\d/.test(str[i])) {
              sepIndexes.push(i);
            }
          }
          if (sepIndexes.length === 0) return;
          // assume the last separator is the decimal
          const decIndex = sepIndexes[sepIndexes.length - 1];

          // cut the substr to the separator with replacing non-digit chars
          const intSubstr = str.slice(0, decIndex).replace(/[^\d]/g, '');
          // cut the substr after the separator with replacing non-digit chars
          const decSubstr = str.slice(decIndex + 1).replace(/[^\d]/g, '');

          if (!intSubstr) return;

          const amount = decSubstr ? `${intSubstr}.${decSubstr}` : intSubstr;
          // to get floating point number
          const number = parseFloat(amount);

          if (!isNaN(number)) {
            results.push(String(number));
          }
        });

        return results;
      }

      function loadScript(src, attributes) {
        return new Promise((resolve, reject) => {
          const scriptTag = document.createElement('script');

          if (attributes) {
            Object.keys(attributes)
                    .forEach(key => {
                      scriptTag.setAttribute(key, attributes[key]);
                    });
          }

          scriptTag.type = 'text/javascript';
          scriptTag.src = src;

          scriptTag.onload = () => resolve();
          scriptTag.onerror = event => {
            reject(event);
          };

          document.head.appendChild(scriptTag);
        });
      }

      function renderMessage(objectParams) {
        const paypalSdkScriptConfig = getPayPalSdkScriptConfig();

        const options = transformConfig(paypalSdkScriptConfig.options);
        const attributes = transformConfig(paypalSdkScriptConfig.attributes);

        const messageProps = { placement: 'product', ...objectParams };

        const renderPayPalMessagesComponent = (paypal) => {
          paypal.Messages(messageProps).render('#paypal-commerce-proddetails-messaging-banner');
        };

        if (options['client-id'] && options['merchant-id']) {
          if (!window.paypalMessages) {
            const paypalSdkUrl = 'https://www.paypal.com/sdk/js';
            const scriptQuery = new URLSearchParams(options).toString();
            const scriptSrc = `${paypalSdkUrl}?${scriptQuery}`;

            loadScript(scriptSrc, attributes).then(() => {
              renderPayPalMessagesComponent(window.paypalMessages);
            });
          } else {
            renderPayPalMessagesComponent(window.paypalMessages)
          }
        }
      }

      function transformConfig(config) {
        let transformedConfig = {};

        const keys = Object.keys(config);

        keys.forEach((key) => {
          const value = config[key];

          if (
                  value === undefined ||
                  value === null ||
                  value === '' ||
                  (Array.isArray(value) && value.length === 0)
          ) {
            return;
          }

          transformedConfig = {
            ...transformedConfig,
            [key]: Array.isArray(value) ? value.join(',') : value,
          };
        });

        return transformedConfig;
      }

      function getPayPalSdkScriptConfig() {
        const attributionId = '{{ ../attribution_id }}';
        const currencyCode = '{{ merchant.currency }}';
        const clientId = '{{ merchant.clientId }}';
        const merchantId = '{{ merchant.merchantId}}';
        const isDeveloperModeApplicable = '{{ merchant.isDeveloperModeApplicable }}'.indexOf('true') !== -1;
        const buyerCountry = '{{ merchant.buyerCountry }}';

        return {
          options: {
            'client-id': clientId,
            'merchant-id': merchantId,
            components: ['messages'],
            currency: currencyCode,
            ...(isDeveloperModeApplicable && { 'buyer-country': buyerCountry }),
          },
          attributes: {
            'data-namespace': 'paypalMessages',
            'data-partner-attribution-id': attributionId,
          },
        };
      }
    })();
  </script>
</div>
{{/if}}
{{/each}}

