widgetTemplates.storefront_api_query:
query Product($productId: Int, $activeCurrencyCode: currencyCode!) {
  site {
    settings {
      storeName
      tax {
        plp
      }
    }
    currency(currencyCode: $activeCurrencyCode) {
      display {
        symbol
        symbolPlacement
        decimalToken
        decimalPlaces
        thousandsToken
      }
    }
    product(entityId: $productId) {
      name
      entityId
      addToCartUrl
      addToWishlistUrl
      brand {
        name
      }
      path
      sku
      priceWithTax: prices(includeTax: true, currencyCode: $activeCurrencyCode) {
        price {
          currencyCode
          value
        }
      }
      priceWithoutTax: prices(includeTax: false, currencyCode: $activeCurrencyCode) {
        price {
          currencyCode
          value
        }
      }
      reviewSummary {
        summationOfRatings
        numberOfReviews
      }
      defaultImage {
        url(width: 1500, height: 1500)
      }
    }
  }
}

query ProductSet($productIds: [Int!], $activeCurrencyCode: currencyCode!) {
  site {
    settings {
      storeName
      tax {
        plp
      }
    }
    currency(currencyCode: $activeCurrencyCode) {
      display {
        symbol
        symbolPlacement
        decimalToken
        decimalPlaces
        thousandsToken
      }
    }
    products(entityIds: $productIds) {
      edges {
        node {
          brand {
            name
          }
          name
          entityId
          addToCartUrl
          addToWishlistUrl
          path
          sku
          priceWithTax: prices(includeTax: true, currencyCode: $activeCurrencyCode)  {
            price {
              currencyCode
              value
            }
          }
          priceWithoutTax: prices(includeTax: false, currencyCode: $activeCurrencyCode)  {
            price {
              currencyCode
              value
            }
          }
          reviewSummary {
            summationOfRatings
            numberOfReviews
          }
          defaultImage {
            url80wide: url(width: 80)
            url160wide: url(width: 160)
            url320wide: url(width: 320)
            url640wide: url(width: 640)
            url960wide: url(width: 960)
            url1280wide: url(width: 1280)
            url1920wide: url(width: 1920)
          }
        }
      }
    }
  }
}

