{{#if currency '===' _.context.activeCurrencyCode}}
<div id="banner-region-{{ _.id }}" style="width: {{#if product_banner_width}}{{product_banner_width}}{{else}}100%{{/if}}; height: {{#if product_banner_height}}{{product_banner_height}}{{else}}50px{{/if}}">
    <script defer nonce="{{nonce}}">
        _affirm_config = {
            public_api_key: "{{ public_api_key }}",
            script:
                {{#if test_mode }}
                    "https://cdn1-sandbox.affirm.com/js/v2/affirm.js"
                {{else}}
                    "https://cdn1.affirm.com/js/v2/affirm.js"
                {{/if}}
        };
        (function(l,g,m,e,a,f,b){var d,c=l[m]||{},h=document.createElement(f),n=document.getElementsByTagName(f)[0],k=function(a,b,c){return function(){a[b]._.push([c,arguments])}};c[e]=k(c,e,"set");d=c[e];c[a]={};c[a]._=[];d._=[];c[a][b]=k(c,a,b);a=0;for(b="set add save post open empty reset on off trigger ready setProduct".split(" ");a<b.length;a++)d[b[a]]=k(c,e,b[a]);a=0;for(b=["get","token","url","items"];a<b.length;a++)d[b[a]]=function(){};h.async=!0;h.src=g[f];n.parentNode.insertBefore(h,n);delete g[f];d(g);l[m]=c})(window,_affirm_config,"affirm","checkout","ui","script","ready");

        affirm.ui.ready(function() {
            "use strict";

            const priceSelectors = [{{{json product_banner_price_selector }}}, '.productView-price .price--withoutTax .price', '.productView-price .price--withoutTax .price'];

            const priceElement = priceSelectors.reduce((acc, selector) => {
                return acc || (selector ? document.querySelector(selector) : null);
            }, null);

            if (!priceElement) {
                console.warn('Affirm: Price element not found on the page');
                return;
            }

            const affirmBanner = document.getElementById('affirm-widget');

            /**
             * Converts a numeric string representation of a price to cents.
             * The function handles the following input formats:
             * - Standard prices with thousands separators and decimal separators (e.g., "1,234.56").
             * - Prices in the form of a range (e.g., "12,345.67-14,567.89"), returning the first price in cents.
             * - Input strings may include symbols (e.g., "$", "USD") that will be removed.
