        };

        if (options['client-id'] && options['merchant-id']) {
          if (!window.paypalMessages) {
            const paypalSdkUrl = 'https://www.paypal.com/sdk/js';
            const scriptQuery = new URLSearchParams(options).toString();
            const scriptSrc = `${paypalSdkUrl}?${scriptQuery}`;

            loadScript(scriptSrc, attributes).then(() => {
              renderPayPalMessagesComponent(window.paypalMessages);
            });
          } else {
            renderPayPalMessagesComponent(window.paypalMessages)
          }
        }
      }

      function transformConfig(config) {
        let transformedConfig = {};

        const keys = Object.keys(config);

        keys.forEach((key) => {
          const value = config[key];

          if (
            value === undefined ||
            value === null ||
            value === '' ||
            (Array.isArray(value) && value.length === 0)
          ) {
            return;
          }

          transformedConfig = {
            ...transformedConfig,
            [key]: Array.isArray(value) ? value.join(',') : value,
          };
        });

        return transformedConfig;
      }

      function getPayPalSdkScriptConfig() {
        const attributionId = '{{ ../attribution_id }}';
        const currencyCode = '{{ merchant.currency }}';
        const clientId = '{{ merchant.clientId }}';
        const merchantId = '{{ merchant.merchantId}}';
        const isDeveloperModeApplicable = '{{ merchant.isDeveloperModeApplicable }}'.indexOf('true') !== -1;
        const buyerCountry = '{{ merchant.buyerCountry }}';

        return {
          options: {
            'client-id': clientId,
            'merchant-id': merchantId,
            components: ['messages'],
            currency: currencyCode,
            ...(isDeveloperModeApplicable && { 'buyer-country': buyerCountry }),
          },
          attributes: {
            'data-namespace': 'paypalMessages',
            'data-partner-attribution-id': attributionId,
          },
        };
      }
    })();
  </script>
</div>
{{/if}}
{{/each}}
